package cn.wen.oauth.config;import cn.wen.oauth.enums.AuthResponseStatus;import cn.wen.oauth.exception.AuthException;import cn.wen.oauth.request.AuthDefaultRequest;/** * @ClassName: AuthSource * @Author: 小飞 * @Date: 2023/5/9 20:33 * @Description: OAuth 平台的API 地址统一接口 */public interface AuthSource {    /**     * 授权的api，必须实现     *     * @return url     */    String authorize();    /**     * 获取accessToken的api 必须实现     *     * @return url     */    String accessToken();    /**     * 获取用户信息的api 必须实现     *     * @return url     */    String userInfo();    /**     * 取消授权的api 非必须实现接口     *     * @return url     */    default String revoke() {        throw new AuthException(AuthResponseStatus.UNSUPPORTED);    }    /**     * 刷新授权的api 非必须实现接口     *     * @return url     */    default String refresh() {        throw new AuthException(AuthResponseStatus.UNSUPPORTED);    }    /**     * 获取Source的字符串名字     *     * @return name     */    default String getName() {        if (this instanceof Enum) {            return String.valueOf(this);        }        return this.getClass().getSimpleName();    }    /**     * 平台对应的 AuthRequest 实现类，必须继承自 {@link AuthDefaultRequest}     *     * @return class     */     Class<? extends AuthDefaultRequest> getTargetClass();}