package cn.wen.oauth.enums.scope;/** * @ClassName: AuthScope * @Author: 小飞 * @Date: 2023/5/9 20:37 * @Description: 各个平台 Scope 类的统一接口 （授权范围） */public interface AuthScope {    /**     * 获取字符串 {@code scope}，对应为各平台实际使用的 {@code scope}     *     * @return String     */    String getScope();    /**     * 判断当前 {@code scope} 是否为各平台默认启用的     *     * @return boolean     */    boolean isDefault();}