package cn.wen.oauth.request;import cn.wen.oauth.cache.AuthStateCache;import cn.wen.oauth.config.AuthConfig;import cn.wen.oauth.config.AuthDefaultSource;import cn.wen.oauth.entity.AuthCallback;import cn.wen.oauth.entity.AuthToken;import cn.wen.oauth.entity.AuthUser;import cn.wen.oauth.enums.AuthUserGender;import cn.wen.oauth.enums.scope.AuthGoogleScope;import cn.wen.oauth.exception.AuthException;import cn.wen.oauth.utils.AuthScopeUtils;import cn.wen.oauth.utils.HttpUtils;import cn.wen.oauth.utils.UrlBuilder;import com.alibaba.fastjson.JSONObject;import com.xkcoding.http.support.HttpHeader;/** * @ClassName: AuthGoogleRequest * @Author: 小飞 * @Date: 2023/5/13 13:19 * @Description: 谷歌登录授权请求处理类 */public class AuthGoogleRequest extends AuthDefaultRequest {    public AuthGoogleRequest(AuthConfig config) {        super(config, AuthDefaultSource.GOOGLE);    }    public AuthGoogleRequest(AuthConfig config, AuthStateCache authStateCache) {        super(config, AuthDefaultSource.GOOGLE, authStateCache);    }    @Override    protected AuthToken getAccessToken(AuthCallback authCallback) {        String response = doPostAuthorizationCode(authCallback.getCode());        JSONObject accessTokenObject = JSONObject.parseObject(response);        this.checkResponse(accessTokenObject);        return AuthToken.builder()                .accessToken(accessTokenObject.getString("access_token"))                .expireIn(accessTokenObject.getIntValue("expires_in"))                .scope(accessTokenObject.getString("scope"))                .tokenType(accessTokenObject.getString("token_type"))                .idToken(accessTokenObject.getString("id_token"))                .build();    }    @Override    protected AuthUser getUserInfo(AuthToken authToken) {        HttpHeader head = new HttpHeader();        head.add("Authorization",  "Bearer " + authToken.getAccessToken());        String userInfo = new HttpUtils(config.getHttpConfig()).post(userInfoUrl(authToken), null, head)                .getBody();        JSONObject object = JSONObject.parseObject(userInfo);        this.checkResponse(object);        return AuthUser.builder()                .rawUserInfo(object)                .uuid(object.getString("sub"))                .username(object.getString("email"))                .avatar(object.getString("picture"))                .nickname(object.getString("name"))                .location(object.getString("locale"))                .email(object.getString("email"))                .gender(AuthUserGender.UNKNOWN)                .token(authToken)                .source(source.toString())                .build();    }    @Override    public String authorize(String state) {        return UrlBuilder.fromBaseUrl(super.authorize(state))                .queryParam("access_type", "offline")                .queryParam("scope", this.getScopes(" ",                        false, AuthScopeUtils.getDefaultScopes(AuthGoogleScope.values())))                .queryParam("prompt", "select_account")                .build();    }    @Override    protected String userInfoUrl(AuthToken authToken) {        return UrlBuilder.fromBaseUrl(source.userInfo())                .queryParam("access_token", authToken.getAccessToken()).build();    }}