package io.github.codergjw.config;import io.github.codergjw.log.Log;/** * @ClassName: YaLingAuthLogConfig * @Author: 小飞 * @Date: 2023/5/9 20:43 * @Description: YaLingAuth 日志配置类 */public class YaLingAuthLogConfig {    /**     * 设置日志级别     *     * @param level 日志级别     */    public static void setLevel(Log.Level level) {        Log.Config.level = level;    }    /**     * 关闭日志     */    public static void disable() {        Log.Config.enable = false;    }    /**     * 开启日志     */    public static void enable() {        Log.Config.enable = true;    }}